  background-size: cover;
  position: relative;
  border-radius: 0 0 25px 25px;
}
.HeroText {
  position: absolute;
  bottom: 10%;
  left: 10%;
  width: 50%;

  font-size: clamp(2rem, 10vw, 7rem);
  letter-spacing: 0.3rem;
  color: white;
  font-weight: bold;
}
.LinksAdj {
  padding-left: 4rem;
  justify-content: space-between;
}
.LinksAdj .navbar-nav,
li {
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 1rem;

  font-size: 16px;
}
.dropdown {
  justify-content: center;
}
.dropdown .nav-link {
  background-color: rgba(255, 255, 255, 0.1);
  padding: 0.5rem;
  border-radius: 30px;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
}
.dropdown-menu[data-bs-popper] {
  left: -70px !important;
}
.SecondSectionPar {
  background-color: #eef5fa;
  padding: 5rem 2rem;
  position: relative;
}

.SecondSecDet {
  /* width: 90%; */
  border-radius: 40px;
  background-color: white;
  box-shadow: 0px 14px 28px 18px #1c1c1c0a;
  margin: auto;
  /* padding: 4rem 2rem; */
