  .popImage {
    position: absolute;
    right: 0;
    bottom: 0;
    width: fit-content;
    img {
      width: 18vw;
    }
  }
  .devCompany {
    background-color: #fcf3f5;
    padding: 0.5rem;
    border-radius: 30px;
    display: flex;
    justify-content: space-around;
    align-items: center;
  }
  h4,
  h3,
  h2,
  span {
    color: black;
    font-size: 18px;
    line-height: 38.03px;
    font-weight: 400;
  }
  span {
    font-size: 19px;
    font-weight: 700;
    color: #503037;
  }
