    line-height: 46.77px;
  }
  h2 {
    font-weight: 700;
    line-height: 46.77px;
    font-size: 34px;
  }
}
.VisMisSectionCont {
  padding: 5rem 2rem;
  margin: auto;
}

.vismisimgSect {
  background-color: #eef5fa;
  min-width: fit-content;
}
.VisMisSectionMain {
  h2,
  p {
    color: black;
  }
  h2 {
    font-weight: 700;
  }
}
.StragtObj {
  background-color: #f3f8ee;
  padding: 5rem 2rem;
  position: relative;
}
.StragObjCont {
  margin: auto;
  background-color: white;
  padding: 4rem 2rem;
  gap: 2rem;
  border-radius: 40px;
  h2 {
    text-align: center;
    font-weight: 700;
    line-height: 48px;
    size: 40px;
    color: black;
  }
  h3 {
    font-weight: 600;
    font-size: 24px;
    line-height: 29px;
    color: #0a0a09;
  }
}
.straObjBoxParent {
  justify-content: space-between;
  margin: auto;
  row-gap: 2rem;
  .straObjBox {
    border: 2px solid #dae8c9;
    border-radius: 16px;
    min-height: 270px;
    width: calc(100% / 4.5);
    justify-content: space-between;
    padding: 2rem 1.5rem;
    display: flex;
    flex-direction: column;
    justify-content: start;
    align-items: start;
    gap: 2rem;
    row-gap: 2rem;
    position: relative;
    ::before {
      content: "";
      width: 100%;
      height: 100%;
      top: 0; /* Start from the top */
      right: 100%;
      transform: translate(100%, -150%) rotate(-90deg); /* Rotate from above */
      background: url(../images/HoverEffect.png) no-repeat center center;
      position: absolute;
      transition: transform 0.5s ease;
      opacity: 0; /* Hide initially */
    }
  }
}
.straObjBox:hover {
  transition: 0.5s linear;
  outline: 5px solid #c0e396;
  padding-bottom: 1rem;
  justify-content: space-between;
  background-color: #dae8c97e;
  position: relative;
  ::before {
    top: 25%;
    right: 20%;
    transform: translate(50%, -50%) rotate(0deg); /* Rotate into place */
    opacity: 1; /* Show on hover */
  }
}
@media (max-width: 1280px) {
  .straObjBoxParent {
    .straObjBox {
      min-width: calc(100% / 2.5);
    }
  }
}
@media (max-width: 676px) {
  .straObjBoxParent {
    .straObjBox {
      min-width: calc(100% / 1);
    }
  }
}
