.slider-container {
  width: 100%;
  /* max-width: 1200px; */
  overflow: hidden;
  position: relative;
}

.slider {
  display: flex;
  transition: transform 0.5s ease-in-out;
}
.SliderCont {
  position: relative;
}
.slide {
  min-width: calc(100%);
  background: #fff;
  color: black;
  border-radius: 12px;
  /* margin-right: 1rem; */
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
  position: relative;
  background-color: #ffffff14;
  border: 1px solid white;
  .SlideContent {
    position: absolute;
    top: 10%;
