}

.controls {
  position: absolute;
  top: 50%;
  display: flex;
  justify-content: space-between;
  /* width: 100%; */
  right: 10%;
  transform: translateY(-50%);
  gap: 2rem;
}

.prev,
.next {
  background: rgba(0, 0, 0, 0.2);
  color: #fff;
  border: 1px solid white;
  padding: 15px 20px;
  border-radius: 50%;
