.Gallery {
  margin: 140px 0;
  div {
    color: black;
    font-size: 64px;
    font-weight: 700;
    line-height: 84px;
  }
  grid-template-columns: auto auto auto auto;
  grid-template-rows: 89px 89px 89px 89px 89px;
}
.imgCont {
  position: relative;
  overflow: hidden;
  cursor: pointer;
}

.imgCont::before {
  content: "";
  position: absolute;
  top: 50%;
  left: 50%;
  width: 100%; /* Adjust size for the hover effect */
  height: 100%;
  background-image: url(../images/zoomin.png);
  background-size: cover;
  background-position: center;
  transform: translate(-50%, -50%) scale(0);
  opacity: 0;
  transition: transform 0.3s ease, opacity 0.3s ease;
  pointer-events: none;
  z-index: 2;
}

.imgCont:hover::before {
  transform: translate(-50%, -50%) scale(1);
  opacity: 1;
}

.imgCont:hover img {
  transition: transform 0.3s ease;
}

/* .container {
  display: flex;
  justify-content: space-between;
  gap: 10px;
  height: 500px;
} */

.column {
  display: grid;
  gap: 5px;
}

.column-1 {
  grid-template-rows: 1fr 1fr;
}

.column-2 {
  grid-template-rows: 3fr 1fr;
}

/* 3rd column: top is 1/4 and bottom is 3/4 */
.column-3 {
  grid-template-rows: 2fr 3fr;
}

/* 4th column: equal halves */
.column-4 {
  grid-template-rows: 1fr 1fr;
}
@media (max-width: 767px) {
  .container {
    height: 200px;
  }
  .Gallery {
    div {
      font-size: 18px !important;
      font-weight: 700;
      line-height: 84px;
    }
  }
  .column-2 {
    grid-template-rows: 2fr 1fr;
  }
}
/* Optional styling for grid items */
.item {
  display: flex;
  align-items: start;
  justify-content: start;
  font-family: sans-serif;
  font-size: 1.2rem;
  overflow: hidden;
}

.item img {
  width: 100%;
  height: 100%;
  object-fit: fill; /* or use 'contain' based on your design needs */
  display: block;
}
.modal {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  opacity: 0;
  pointer-events: none;
  transition: opacity 0.3s ease;
  z-index: 1000;
}
.modal.open {
  opacity: 1;
  pointer-events: auto;
}
.modal img {
  min-width: 40vw;
  min-height: 50vh;
  max-width: 90vw;
  max-height: 90vw;
  box-shadow: 0 0 15px rgba(255, 255, 255, 0.3);
}
.modal .close {
  position: absolute;
  top: 20px;
  right: 30px;
  color: #fff;
