+      // Validation functions
+      function validateEmail() {
+        const emailValue = emailInput.value.trim();
+        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
+        let valid = true;
+        if (emailValue === "") {
+          emailError.textContent = "Email is required.";
+          emailError.classList.remove("hidden");
+          emailInput.classList.add("border-red-500");
+          valid = false;
+        } else if (!emailRegex.test(emailValue)) {
+          emailError.textContent = "Please enter a valid email.";
+          emailError.classList.remove("hidden");
+          emailInput.classList.add("border-red-500");
+          valid = false;
+        } else {
+          emailError.textContent = "";
+          emailError.classList.add("hidden");
+          emailInput.classList.remove("border-red-500");
+          valid = true;
+        }
+        return valid;
+      }
+      function validateDate() {
+        const dateValue = dateInput.value;
+        let valid = true;
+        if (dateValue === "") {
+          dateError.textContent = "Date is required.";
+          dateError.classList.remove("hidden");
+          dateInput.classList.add("border-red-500");
+          valid = false;
+        } else {
+          dateError.textContent = "";
+          dateError.classList.add("hidden");
+          dateInput.classList.remove("border-red-500");
+          valid = true;
+        }
+        return valid;
+      }
+      function validateNotes() {
+        const notesValue = notesInput.value.trim();
+        let valid = true;
+        if (notesValue === "") {
+          notesError.textContent = "Notes are required.";
+          notesError.classList.remove("hidden");
+          notesInput.classList.add("border-red-500");
+          valid = false;
+        } else {
+          notesError.textContent = "";
+          notesError.classList.add("hidden");
+          notesInput.classList.remove("border-red-500");
+          valid = true;
+        }
+        return valid;
+      }
+      function validateForm() {
+        let isValid = true;
+        let firstErrorField = null;
+        if (!validateEmail()) {
+          isValid = false;
+          firstErrorField = firstErrorField || emailInput;
+        }
+        if (!validateDate()) {
+          isValid = false;
+          firstErrorField = firstErrorField || dateInput;
+        }
+        if (!validateNotes()) {
+          isValid = false;
+          firstErrorField = firstErrorField || notesInput;
+        }
+        if (!isValid && firstErrorField) {
+          firstErrorField.focus();
+        }
+        return isValid;
